class Solution {
public:
    int sumCounts(vector<int>& nums) {
        int n = nums.size();
        int sum[n * 4], todo[n * 4];
        memset(sum, 0, sizeof sum);
        memset(todo, 0, sizeof todo);

        auto do_ = [&](int o, int l, int r, int add) {
            sum[o] += add * (r - l + 1);
            todo[o] += add;
        };

        // o=1  [l,r] 1<=l<=r<=n
        // 把 [L,R] 加一，同时返回加一之前的区间和
        auto query_and_add1 = [&](int o, int l, int r, int L, int R) {
            if (L <= l && r <= R) {
                do_(o, l, r, 1);
                return sum[o];
            }
            int m = l + r >> 1;
            if (L <= m) {
                return query_and_add1(o << 1, l, m, L, R);
            }
            if (m < R) {
                return query_and_add1(o << 1 | 1, m + 1, r, L, R);
            }
            return 0;
        };

        int ans = 0, s = 0;
        unordered_map<int, int> last;
        for (int i = 0; i < n; i++) {
            int x = nums[i];
            int j = last[x];
            s += query_and_add1(1, 1, n, j + 1, i) * 2 + i - j;
            ans += s;
            last[x] = i;
        }
        return ans % 1e9 + 1e9;
    }

