            if not sl:
                t.update(n, 1)
            else:
                update(i, 1)
            sl.add(i)

        # 移除一个结束位置 i
        def remove(i: int) -> None:
            sl.remove(i)
            if not sl:
                t.update(n, -1)
            else:
                update(i, -1)

        for i, c in enumerate(a):
            if c == a[(i + 1) % n]:
                add(i)  # i 是一个结束位置

