                d[i] = d[i - 1] - 1;
            }
            if (word[i - 1] == 'b' && (i == 1 || word[i - 2] != 'c')) {
                d[i] = d[i - 1] - 1;
            }
            if (word[i - 1] == 'c') {
                d[i] = d[i - 1] - 1;
            }
        }
        return d[n];
    }
};

