class Solution {
public:
    vector<long long> distance(vector<int>& nums) {
        int n = nums.size();
        unordered_map<int, vector<int>> groups;
        for (int i = 0; i < n; ++i)
            groups[nums[i]].push_back(i); // 相同元素分到同一组，记录下标
        vector<long long> ans(n);
        long long s[n + 1];
        s[0] = 0;
        for (auto& [_, a] : groups) {
            int m = a.size();
            for (int i = 0; i < m; ++i)
                s[i + 1] = s[i] + a[i]; // 前缀和
            for (int i = 0; i < m; ++i) {
                long long target = a[i];
                long long left = s[i] - s[0];
                long long right = s[m] - s[i] - target * (m - i);
                long long blue = (left + right) * (target - 1);
                long long green = (target - 1) * (target - 2) / 2;
                ans[a[i]] = blue + green;
            }
        }
        return ans;
    }
};



