class Solution {
    private static final int MX = 31622;
    private static final int[] PI = new int[MX + 1];

    static {
        for (int i = 2; i <= MX; i++) {
            if (PI[i] == 0) { // i 是质数
                PI[i] = PI[i - 1] + 1;
                for (int j = i * i; j <= MX; j += i) {
                    PI[j] = -1; // 标记 i 的倍数为合数
                }
            } else {
                PI[i] = PI[i - 1];
            }
        }
    }

    public int nonSpecialCount(int l, int r) {
        int cnt = 0;
        for (int i = l; i <= r; i++) {
            int n = i;
            int s = 0;
            for (int j = 2; j <= Math.sqrt(n); j++) {
                if (n % j == 0) {
                    s += PI[j];
                    if (j * j != n) {
                        s += PI[n / j];
                    }
                }
            }
            if (s == 0) {
                cnt++;
            }
        }
        return cnt;
    }
}
