   def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
       fa = list(range(n - 1))

       # 非递归并查集
       def find(x: int) -> int:
           rt = x
           while fa[rt] != rt:
               rt = fa[rt]
           while fa[x] != rt:
               fa[x], x = rt, fa[x]
           return rt

