class Solution {
public:
    int sumCounts(vector<int>& nums) {
        int n = nums.size();
        int sum[n * 4];
        int todo[n * 4];
        auto doo = [&](int o, int l, int r, int add) {
            sum[o] += add * (r - l + 1);
            todo[o] += add;
        };
        function<int(int, int, int, int, int)> q;
        q = [&](int o, int l, int r, int L, int R) {
            if (L <= l and r <= R) {
                doo(o, l, r, 1);
                return sum[o];
            }
            int m = (l + r) / 2;
            int add = todo[o];
            if (add) {
                doo(o * 2, l, m, add);
                doo(o * 2 + 1, m + 1, r, add);
                todo[o] = 0;
            }
            int res = 0;
            if (L <= m) res += q(o * 2, l, m, L, R);
            if (m < R) res += q(o * 2 + 1, m + 1, r, L, R);
            sum[o] = sum[o * 2] + sum[o * 2 + 1];
            return res;
        };
        long long ans = 0, s = 0;
        unordered_map<int, int> last;
        for (int i = 0; i < n; ++i) {
            int j = last.count(nums[i]) ? last[nums[i]] : 0;
            s += q(1, 1, n, j + 1, i + 1) * 2 + i - j;
            ans += s;
            last[nums[i]] = i + 1;
        }
        return ans % 1_

