class Solution {
public:
    int minimumCost(string source, string target, vector<string> original, vector<string> changed, vector<int> cost) {
        int n = source.size();
        int m = target.size();
        if (n != m)
            return -1;
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (source[i] != target[i]) {
                int pos = 0;
                while (pos < n) {
                    if (original[pos] == source[i] && changed[pos] == target[i]) {
                        break;
                    }
                    pos++;
                }
                if (pos == n) {
                    return -1;
                }
                ans += cost[pos];
            }
        }
        return ans;
    }
};

