import java.util.Arrays;
import java.util.Vector;

class BIT {
    private int n;
    private int[] tree;

    public BIT(int n) {
        this.n = n;
        tree = new int[n + 1];
    }

    public void inc(int i) {
        while (i < tree.length) {
            tree[i]++;
            i += i & -i;
        }
    }

    public int sum(int x) {
        int res = 0;
        while (x > 0) {
            res += tree[x];
            x &= x - 1;
        }
        return res;
    }

    public int query(int left, int right) {
        return sum(right) - sum(left - 1);
    }
}

class Solution {
    public long countOperationsToEmptyArray(Vector<Integer> nums) {
        int n = nums.size();
        int[] id = new int[n];
        for (int i = 0; i < n; i++) id[i] = i;
        Arrays.sort(id, (a, b) -> Integer.compare(nums.get(a), nums.get(b)));
        long ans = n;
        BIT t = new BIT(n + 1);
        int pre = 1;
        for (int k = 0; k < n; k++) {
            int i = id[k] + 1;
            if (i >= pre) {
                t.inc(i);
                pre = i;
            } else {
                t.inc(n);
                t.inc(i);
                pre = i;
            }
        }
        return ans;
    }
}

