class Solution {
public:
    vector<int> getSubarrayBeauty(vector<int> &nums, int k, int x) {
        const int BIAS = 50;
        int cnt[BIAS * 2 + 1]{}, n = nums.size();
        vector<int> ans(n - k + 1);
        for (int i = 0; i < k - 1; ++i) // 先往窗口内添加 k-1 个数
            ++cnt[nums[i] + BIAS];
        for (int i = k - 1; i < n; ++i) {
            ++cnt[nums[i] + BIAS]; // 进入窗口（保证窗口有恰好 k 个数）
            int left = x;
            for (int j = 0; j < BIAS; ++j) {
                // 暴力枚举负数范围 [-50,-1]
                if (left <= 0) {
                    // 找到美丽值
                    ans[i - k + 1] = j - BIAS;
                    break;
                }
                left -= cnt[j];
            }
            --cnt[nums[i - k + 1] + BIAS]; // 离开窗口
        }
        return ans;
    }
};

class Solution {
public:
    int addMinimum(string word) {
        int n = word.size();
        vector<int> d(n + 1);
        for (int i = 1; i <= n; i++) {
            if (word[i - 1] == 'a') d[i] = d[i - 1] + 1;
            else if (word[i - 1] == 'b') d[i] = d[i - 1] - 1;
            else if (word[i - 1] == 'c') d[i] = d[i - 1] - 2;
            else d[i] = 0;
        }
        return d[n];
    }
