public:
    int minOrAfterOperations(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = 0, mask = 0;
        for(int b = 29; b >= 0; b--) {
            mask |= 1 << b;
            int cnt = 0, and = -1;
            for(auto x: nums) {
                and &= x & mask;
                if(and != 0) cnt++;
                else and = -1;
            }
            if(cnt > k) {
                ans |= mask;
                mask ^= 1 << b;
            }
        }
        return ans;
    }
};

