            long h = 0;
            for (char b : words[i].toCharArray()) {
                h = (h * BASE + b) % MOD;
            }
            minCost.computeIfAbsent(words[i].length(), k -> new HashMap<>())
                   .merge((int) h, costs[i], Integer::min);
        }

        int[] f = new int[n + 1];
