class Solution {
public:
    vector<int> getSubarrayBeauty(vector<int> &nums, int k, int x) {
        const int BIAS = 50;
        int cnt[BIAS * 2 + 1]{};
        int n = nums.size();
        vector<int> ans(n - k + 1);

        for (int i = 0; i < k - 1; ++i) {
            // 先往窗口内添加 k-1 个数
            ++cnt[nums[i] + BIAS];
        }

        for (int i = k - 1; i < n; ++i) {
            // 进入窗口（保证窗口有恰好 k 个数）
            ++cnt[nums[i] + BIAS];

            int left = x;
            for (int j = 0; j < BIAS; ++j) {
                // 暴力枚举负数范围 [-50,-1]
                if (left <= 0) {
                    // 找到美丽值
                    ans[i - k + 1] = j - BIAS;
                    break;
                }
                if (cnt[j] != 0) {
                    --left;
                }
            }

            // 离开窗口
            --cnt[nums[i - k + 1] + BIAS];
        }

        return ans;
    }
};

