public int nonSpecialCount(int l, int r) {
    int ans = 0;
    for (int i = l; i <= r; i++) {
        if (PI[i] == 2) ans++; // i 的因子不够多
        else if (PI[i] == 1) {
            boolean special = false;
            int j = 1;
            int n = i;
            while (j * j <= n) {
                if (n % j == 0) {
                    if (j + n / j > r) {
                        special = true;
                        break;
                    }
                }
                j++;
            }
            if (!special) ans++;
        }
    }
    return ans;
}

