class Solution {
    public int maximumLength(int[] arr) {

        Arrays.sort(arr);
        boolean[] vis = new boolean[arr.length+1];  

        int count = 0; 
        int i = 0, onesCount = 0; 
        for(i = 0; i< arr.length && arr[i] == 1; i++ ){
            onesCount++; 
        }

        if(onesCount%2 == 0) count = onesCount-1; 
        else count = onesCount;  

        // till here we have calculated number of ones
        // and our i is at the first index where our element is != 1

         while( i < arr.length ){

             if( !vis[i] ){

                // If not visited find it's square sequence
                int currCount = 0;  

                int j = i; 
                while( j != -1){

                    vis[j] = true; 
                   // here either we reach end  or the count of square 
                   // is only 1 , hence our sequence end s
                    if( j == arr.length-1 || arr[j] != arr[j+1] ){
                        currCount++; 
                        break; 
                    }   
                    vis[j+1] = true; 

                   // use binary search to find next square 
                   j = binary(arr,j+2,arr[j]*arr[j]);
                   currCount += 2;  
                }

                // now if our count comes out to be even 
                // which is poissible [ 2,2,4,4] 
                // then we only take count-1 that is [ 2,2,4]
                if( currCount%2 == 0 ) count = Math.max(count,currCount-1); 
                 else count = Math.max(count,currCount);
               }
            i++; 
         }

         return count;

    }

      public int binary(int[] arr,int start,int target){

        int end = arr.length-1; 
        int ans = -1; 

        while( start <= end ){
            int middle = (start+end)/2; 

            if( arr[middle] == target ){
                ans = middle; 
                end = middle-1; 
            }else if( arr[middle]> target ) end = middle-1; 
            else start = middle+1; 
        }

        return ans;
    }

