#include <iostream>
#include <vector>
using namespace std;

int countSubmatrices(vector<vector<int>>& grid, int k) {
    int ans = 0;
    for(int i = 0; i < grid.size(); ++i){
        for(int j = 0; j < grid[0].size(); ++j){
            if(i >= 1) grid[i][j] += grid[i-1][j];
            if(j >= 1) grid[i][j] += grid[i][j-1];
            if(i >=1 && j >=1) grid[i][j] -= grid[i-1][j-1];
            if(grid[i][j] <= k) ans++;
        }
    }
    return ans;
}
int main(){
    vector<vector<int>>grid(2,vector<int>(2,0));
    grid[0][0] = 1;
    grid[0][1] = 2;
    grid[1][0] = 3;
    grid[1][1] = 4;
    int k = 10;
    cout << countSubmatrices(grid,k) << endl;
    return 0;
}

