class Solution {
public:
    int minInsertions(string s) {
      stack<char> stack;
      int count = 0;
      for(int i = 0; i < s.size(); i++) {
        char temp = s[i];
        if(temp == '(') {
          stack.push(temp);
        }
        else {
          if(stack.empty()) {
            count++;
            stack.push(temp);
          }
          else {
            stack.pop();
          }
        }
      }
      while(!stack.empty()) {
        count++;
        stack.pop();
      }
      return count;
    }
};

