class Solution {
public:
    int maxOperations(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> memo(n, vector<int>(n, -1));
        function<int(int,int,int)> dp = [&](int prev, int l, int r) {
            if (r - l + 1 < 2)
                return 0;
            if (memo[l][r] != -1)
                return memo[l][r];
            int op1Score = nums[l] + nums[l+1];
            int op2Score = nums[r-1] + nums[r];
            int op3Score = nums[l] + nums[r];
            int op1 = op1Score == prev ? 1 + dp(prev, l+2, r) : 0;
            int op2 = op2Score == prev ? 1 + dp(prev, l, r-2) : 0;
            int op3 = op3Score == prev ? 1 + dp(prev, l+1, r-1) : 0;
            return memo[l][r] = max({op1, op2, op3});
        };
        return 1 + max({
            dp(nums[0]+nums[1], 2, n-1),
            dp(nums[n-2]+nums[n-1], 0, n-3),
            dp(nums[0]+nums[n-1], 1, n-2),
        });
    }
};

