        int n=v.size();
        int a=0,b=0,c=0;
        for(int i=0;i<n;i++) {
            for(int j=0;j<n;j++) {
                if(v[i][j]==0)
                    a++;
                else if(v[i][j]==1)
                    b++;
                else
                    c++;
            }
        }
        int ans=INT_MAX;
        for(int i=0;i<=2;i++) {
            int costY = a+b+c-a;
            int costRem=INT_MAX;
            for(int j=0;j<=2;j++) {
                if(i==j)
                    continue;
                int t = a+b+c-a;
                costRem = min(costRem,t);
            }
            ans=min(ans,costY+costRem);
        }
        return ans;
    }
