class Solution {
public:
    vector<int> countPrefixSuffixPairs(vector<string> arr, int n) {
        // code here
        vector<int>count(n,0);
        vector<int>ans;
        for(int i=0;i<n;i++){
            string s=arr[i];
            string rev=s;
            reverse(rev.begin(),rev.end());
            for(int j=0;j<s.length();j++){
                if(rev.substr(0,j+1).compare(s.substr(s.length()-j-1,s.length()))==0){
                    count[i]+=1;
                }
            }
        }
        for(int i=0;i<n;i++){
            ans.push_back(count[i]);
        }
         return ans;
    }
};

