class Solution {
public:
    vector<int> resultArray(vector<int>& nums) {
        int n = nums.size();
        int *left = new int[n];
        int *right = new int[n];
        int *left2 = new int[n];
        int *right2 = new int[n];
        for (int i = 0; i < n; i++) {
            int pos;
            if (nums[i] < nums[n-1]) {
                pos = upper_bound(nums.begin()+i, nums.end(), nums[i]) - nums.begin();
                right[pos] = nums[i];
            }
            else {
                pos = lower_bound(nums.begin(), nums.begin()+i, nums[i]) - nums.begin();
                left[pos] = nums[i];
            }
        }
        sort(nums.begin(), nums.begin()+n);
        for (int i = 0; i < n; i++) {
            int pos;
            if (nums[i] < nums[n-1]) {
                pos = upper_bound(nums.begin()+i, nums.end(), nums[i]) - nums.begin();
                right2[pos] = nums[i];
            }
            else {
                pos = lower_bound(nums.begin(), nums.begin()+i, nums[i]) - nums.begin();
                left2[pos] = nums[i];
            }
        }
        for (int i = 0; i < n; i++) {
            if (left[i]) left[i] = left2[i];
            if (right[i]) right[i] = right2[i];
        }
        for (int i = 0; i < n; i++) {
            if (left[i]) nums[i] = left[i];
            else nums[i] = right[i];
        }
        delete[] left;
        delete[] right;
        delete[] left2;
        delete[] right2;
        return nums;
    }
};


