        vector<int> result;
        int len = nums.size();
        while(len > 0) {
            result.push_back(nums[0]);
            nums[0] = -1;

            for(int i=1; i<len; i++) {
                if(nums[i] >= nums[0]) continue;

                while(i < len && nums[i] < nums[0]) {
                    result.push_back(nums[i]);
                    i++;
                }
            }
            len = result.size();
            sort(nums.begin(), nums.begin()+len);
            len = nums.size();
        }
        return result;
    }
};

