//recursion + memo
class Solution {
public:
    int countSubstrings(string s) {
        int n = s.size();
        vector<vector<int>> memo(n, vector<int>(n, -1));
        return helper(s, 0, n - 1, memo);
    }
    int helper(string& s, int l, int r, vector<vector<int>>& memo){
        if(l>r) return 0;
        if(l==r) return 1;
        if(s[l]==s[r] && memo[l][r]==-1) memo[l][r] = 1 + helper(s, l+1, r-1, memo);
        else if(s[l]!=s[r] && memo[l][r]==-1) memo[l][r] = max(helper(s, l+1, r, memo), helper(s, l, r-1, memo));
        return memo[l][r];
    }
};



// dp with center
// dp[i][j] means the number of palindrome substring from s[i] to s[j].
class Solution {
public:
    int countSubstrings(string s) {
        int n = s.size();
        vector<vector<int>> dp(n, vector<int>(n, 1));
        for (int i = 1; i < n; i++) {
            for (int j = 0; j < i; j++) {
                if (s[j] == s[i]) dp[j][i] = 2 + dp[j + 1][i - 1];
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) ans += dp[i][i];
        return ans;
    }
};


