/* Name of the function is det_and_inv
 *
 * Inputs:
 * matrix - a 2-D array
 *
 * returns:
 * a tuple containing a float and a 2-D array
 * 
 * This function takes in an input matrix, calculates the determinant and the inverse 
 * and returns these in the form of a tuple.
 * 
 */

tuple<float,vector<vector<float>>> det_and_inv(vector<vector<float>> matrix){
    // your code here


  	float determinant;
    //if matrix is empty
    if(matrix.empty()){
      	determinant= 0;
		return make_tuple(determinant,matrix);
	}
	//if there's 1 element in the matrix
	else if(matrix.size() == 1 && matrix[0].size() == 1){
		determinant = matrix[0][0];
		return make_tuple(determinant,matrix);
	}
	int size = matrix.size();
	//if there's 2 element in the matrix
	else if (matrix.size() == 2 && matrix[0].size() == 2){
		if (matrix[0][0]*matrix[1][1] == matrix[0][1]*matrix[1][0]){
				determinant = matrix[0][0]*matrix[1][1] - matrix[0][1]*matrix[1][0];
				vector<vector<float>> inv_matrix;
				float determinant_recip = 1/determinant;
				inv_matrix.push_back(vector<float>(1,matrix[1][1]*determinant_recip));
				inv_matrix.push_back(vector<float>(1,-matrix[0][1]*determinant_recip));
				inv_matrix.push_back(vector<float>(1,matrix[0][1]*determinant_recip));
				inv_matrix.push_back(vector<float>(1,-matrix[1][1]*determinant_recip));
			return make_tuple(determinant,inv_matrix);
		}
		else{
			determinant = 0;
			return make_tuple(determinant,matrix);
		}
	}
	//calculate the determinant for any size
	else{
		int sign = 1;
		float determinant = 0;
		vector<vector<float>> inv_matrix;
		vector<vector<float>> sub_matrix;

		//for each element in the row
		for(int i = 0; i < size; i++){
			//for each row in the matrix
			sub_matrix = vector<vector<float>>(matrix.size()-1);
			for(int j = 1; j < size; j++){
				sub_matrix.push_back(vector<float>(matrix.size()-1));
				//for each element in the sub matrix
				for(int k = 0, k1 = 0; k < matrix.size(); k++, k1++){
					if(k != i){
						sub_matrix[j-1].insert(sub_matrix[j-1].begin()+(k1-1), matrix[j][k]);
					}
				}
			}

			//get the determinant for the sub matrix
			auto sub_matrix_det_and_inv = det_and_inv(sub_matrix);
			auto sub_matrix_determinant = get<0>(sub_matrix_det_and_inv);
			sub_matrix = get<1>(sub_matrix_det_and_inv);

			//calculate the determinant
			determinant += matrix[0][i]*sub_matrix_determinant*sign;
			sign = -1*sign;
		}
		return make_tuple(determinant,sub_matrix);
	}

}


