char *str_toupper(char *str)
{
    int len = strlen(str);
    int i;
    char *new = (char *)malloc(sizeof(char) * (len + 1));
    for (i = 0; i < len; ++i)
    {
        new[i] = toupper(str[i]);
    }
    new[len] = '\0';
    return new;
}

