class Solution {
public:
    long largestSquareArea(vector<vector<int>>& bottomLeft, vector<vector<int>>& topRight) {
        long ans = 0;
        for (int i = 0; i < bottomLeft.size(); ++i) {
            for (int j = i + 1; j < topRight.size(); ++j) {
                int x = min(topRight[i][0], topRight[j][0]) - max(bottomLeft[i][0], bottomLeft[j][0]);  // x side intersection
                int y = min(topRight[i][1], topRight[j][1]) - max(bottomLeft[i][1], bottomLeft[j][1]);  // y side intersection
                ans = max(ans, min(x, y));
            }
        }
        return ans * ans;
    }
};


