#include <iostream>
#include <vector>
#include <set>
using namespace std;

class Solution {
public:
    int findChampion(int n, int[][] edges) {
        int[] degree = new int[n];
        for (int[] e : edges) {
            degree[e[1]]++;
        }
        int champion = -1;
        for (int i = 0; i < n; i++) {
            if (degree[i] == 0) {
                if (champion == -1) {
                    champion = i;
                } else {
                    return -1;
                }
            }
        }
        return champion;
    }
};

int main() {
    int n = 5;
    vector<vector<int>> edges = {{0, 1}, {0, 4}, {1, 2}, {2, 3}, {4, 0}, {4, 3}};
    Solution sol;
    cout << sol.findChampion(n, edges);
}

#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    string triangleType(int nums[3]) {
        sort(nums, nums + 3);
        if (nums[0] + nums[1] <= nums[2]) {
            return "none";
        }
        if (nums[0] == nums[2]) {
            return "equilateral";
        }
        if (nums[0] == nums[1] || nums[1] == nums[2]) {
            return "isosceles";
        }
        return "scalene";
    }
};

int main() {
    Solution s;
    int nums[3] = {1, 1, 3};
    cout << s.triangleType(nums) << endl;
}

