def minIncrementOperations(nums: List[int], k: int) -> int:
    dp1 = dp2 = dp3 = 0
    for num in nums:
        dp1, dp2, dp3 = min(dp1, dp2, dp3), dp1, dp2
        if num < k:
            dp1 += 1
        else:
            dp2 += 1
        dp3 += 1
    return min(dp1, dp2, dp3)

