class Solution:
    def maximizeSquareArea(self, m: int, n: int, hFences: List[int], vFences: List[int]) -> int:
        h = self.f(hFences, m)
        v = self.f(vFences, n)
        ans = -1
        for i in h:
            if i in v:
                ans = max(ans, min(i, v[i]))
        return ans ** 2 % 1_000_000_007 if ans != -1 else -1

    def f(self, a: List[int], mx: int) -> Dict[int, int]:
        a.extend([1, mx])
        a.sort()
        d = {}
        for i in range(len(a) - 1):
            d[a[i]] = a[i + 1] - a[i]
        return d

