class Solution:
    def sumCounts(self, nums: List[int]) -> int:
        n = len(nums)
        sum = [0] * (n * 4)
        todo = [0] * (n * 4)

        def do(o: int, l: int, r: int, add: int) -> None:
            sum[o] += add * (r - l + 1)
            todo[o] += add

        # o=1  [l,r] 1<=l<=r<=n
        # 把 [L,R] 加一，同时返回加一之前的区间和
        def query_and_add1(o: int, l: int, r: int, L: int, R: int) -> int:
            if l > R or r < L:
                return 0
            if l >= L and r <= R:
                return sum[o]
            m = (l + r) // 2
            return query_and_add1(o * 2, l, m, L, R) + query_and_add1(o * 2 + 1, m + 1, r, L, R)

        for i in range(n):
            do(1, i, i, 1)

        ans = 0
        for i in range(n):
            for j in range(i, n):
                ans += query_and_add1(1, 0, n - 1, i, j)
        return ans

