class Solution {
public:
    int findChampion(int n, int[][] edges) {
        int[] degree = new int[n];
        for (int[] e : edges) {
            degree[e[1]]++;
        }
        int champion = -1;
        for (int i = 0; i < n; i++) {
            if (degree[i] == 0) {
                if (champion == -1) {
                    champion = i;
                } else {
                    return -1;
                }
            }
        }
        return champion;
    }
};

int main() {
    std::cout << "Testing ...\n";

    std::vector<std::vector<int>> edges;
    std::map<int, std::set<int>> m;
    std::map<int, std::set<int>> m1;

    int n = 4;
    edges = {
        {0, 1}, {1, 2}, {2, 3}
    };

    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            m[i].insert(j);
            m1[j].insert(i);
        }
    }

    for (auto& e : edges) {
        m[e[0]].erase(e[1]);
        m1[e[1]].erase(e[0]);
    }

    int champion = -1;
    for (int i = 0; i < n; ++i) {
        if (m[i].empty()) {
            if (champion == -1) {
                champion = i;
            } else {
                return -1;
            }
        }
    }
    std::cout << champion << std::endl;

    return 0;
}

