/*
 * Input: edges = [[0,1],[1,2],[3,4],[5,6],[7,8],[2,5],[1,8],[4,9],[8,10],[1,7]], values = [10,10,10,10,10,10,10,10,10,10]
 * Output: 20
 * Explanation: The figure above represents the input tree. The blue edges represent the chosen path.
 * The value on each node is in parentheses.
 * The score of the path is: 10 + 10 + 10 + 10 + 10 = 50.
 * Note that the score of the path is not equal to 60.
 * The score will never exceed 60 as the value on the root node is 10.
 */



class Solution {
    public long maximumScoreAfterOperations(int[][] edges, int[] values) {
        List<Integer>[] g = new ArrayList[values.length];
        Arrays.setAll(g, e -> new ArrayList<>());
        g[0].add(-1);
        for (int[] e : edges) {
            int x = e[0], y = e[1];
            g[x].add(y);
            g[y].add(x);
        }
        long ans = 0;
        for (int v : values) {
            ans += v;
        }
        return ans;
    }

    private long dfs(int x, int fa, List<Integer>[] g, int[] values) {
        if (g[x].size() == 1) {
            return values[x];
        }
        long loss = 0;
        for (int y : g[x]) {
            if (y != fa) {
                loss += dfs(y, x, g, values);
            }
        }
        return Math.min(values[x], loss);
    }
}


