long minimumCost(vector<int>& nums, int k, int dist) {
    int n = nums.size();
    vector<long> dp(n + 1, 0);
    vector<int> cnt(n + 1, 0);
    for (int i = 0; i < n; ++i) {
        ++cnt[nums[i]];
        dp[i + 1] = dp[i] + nums[i];
    }
    for (int i = n - 1; i >= 0; --i) {
        dp[i] = min(dp[i], dp[max(i - dist, 0)] + dp[i + 1] - nums[i]);
    }
    return dp[0];
}
