    def __init__(self, n):
        self.ft = [0] * (n + 1)
        self.n = n

    def range_sum(self, i, j):
        return self._range_sum(j) - self._range_sum(i - 1)

    def add(self, i, v):
        while i <= self.n:
            self.ft[i] += v
            i += self._lowbit(i)

