class Solution {
public:
    long long matrixSumQueries(int n, vector<vector<int>> &queries) {
        long long ans = 0;
        unordered_set<int> vis[2];
        vector<long long> row_sums(n), col_sums(n);
        for (int i = queries.size() - 1; i >= 0; i--) {
            auto &q = queries[i];
            int type = q[0], index = q[1], val = q[2];
            if (!vis[type].count(index)) {
                // 后面（>i）没有对这一行/列的操作
                // 这一行/列还剩下 n-vis[type^1].size() 个可以填入的格子____
                vis[type].insert(index);
                ans += (row_sums[index] + col_sums[index] - (long long)val) * (n - vis[type ^ 1].size());
                row_sums[index] += val;
                col_sums[index] += val;
            }
        }
        return ans;
    }
};

