/*
 * Complete the 'findChampion' function below.
 *
 * The function is expected to return an INTEGER.
 * The function accepts following parameters:
 *  1. INTEGER n
 *  2. 2D_INTEGER_ARRAY edges
 */


/*
 * To return the integer -1.
 */
const int INF = -1;

/*
 * To find the champion.
 */
int findChampion(int n, vector<vector<int>>& edges) {

    /*
     * To store the in-degree of each node.
     */
    vector<int> indegree(n, 0);

    /*
     * To traverse the edges to find the champion.
     */
    for (auto edge : edges) {

        /*
         * To get the in-degree of the target node.
         */
        int to = edge[1];

        /*
         * To increase the in-degree of the target node.
         */
        indegree[to]++;
    }

    /*
     * To find the champion.
     */
    int champion = INF;
    for (int i = 0; i < n; i++) {

        /*
         * If the node has no predecessors,
         * it is the champion.
         */
        if (indegree[i] == 0) {
            if (champion == INF) {
                champion = i;
            } else {
                return INF;
            }
        }
    }

    /*
     * Return the champion.
     */
    return champion;
}

