class Solution {
    public long maximumScoreAfterOperations(int[][] edges, int[] values) {
        List<Integer>[] g = new ArrayList[values.length];
        Arrays.setAll(g, e -> new ArrayList<>());
        for (int[] e : edges) {
            int x = e[0], y = e[1];
            g[x].add(y);
            g[y].add(x);
        }
        int[] has = new int[values.length];
        for (int v : values) {
            has[v]++;
        }
        long ans = 0;
        for (int v : values) {
            if (has[v] > 0) {
                ans += v;
                has[v] = 0;
            }
        }
        for (int v : values) {
            ans += dfs(0, -1, g, values, has, v);
        }
        return ans;
    }

    private int dfs(int x, int fa, List<Integer>[] g, int[] values, int[] has, int v) {
        int res = 0;
        for (int y : g[x]) {
            if (y != fa) {
                res += dfs(y, x, g, values, has, v);
            }
        }
        if (res == 0) {
            return v;
        }
        if (has[v] > 0) {
            res += v;
            has[v] = 0;
        }
        return res;
    }
}
\end{code}

