class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        using LL = long long;
        LL t = (LL)target[0] << 32 | target[1];
        unordered_map<LL, int> dis = {{(LL)start[0] << 32 | start[1], 0}, {t, INT_MAX}};
        unordered_set<LL> vis;
        for (;;) {
            LL v = -1;
            int dv = -1;
            for (auto& [p, d] : dis)
                if (!vis.count(p) && (dv < 0 || d < dv))
                    v = p, dv = d;
            if (v == t)
                return dv; // 到终点的最短路已确定
            vis.insert(v);
            int vx = v >> 32, vy = v & UINT32_MAX;// 更新到终点的最短路
            for (auto& r : specialRoads) {
                int d = dis[v] + r[4];// 计算起始点到下一个路口的代价
                LL w = (LL)r[2] << 32 | r[3];
                if (!dis.count(w) || d < dis[w])// 如果该路口不在dist中或者dist[w] > dist[v] + r[4]，更新dist
                    dis[w] = d;
            }
        }
    }
};

