def maximumScoreAfterOperations(edges: List[List[int]], values: List[int]) -> int:
    n = len(values)
    g = [[] for _ in range(n)]
    for u, v in edges:
        g[u].append(v)
        g[v].append(u)
    ans = sum(values)
    for i in range(n):
        val = values[i]
        values[i] = 0
        loss = dfs(i, -1, g, values)
        values[i] = val
        ans = max(ans, loss + val)
    return ans

def dfs(x, fa, g, values):
    if len(g[x]) == 1:
        return values[x]
    loss = 0
    for y in g[x]:
        if y != fa:
            loss += dfs(y, x, g, values)
    return min(values[x], loss)

