class Solution {
    public int[] countOfPairs(int n, int x, int y) {
        int[][] matrix = new int[n][n];
        int INF = Integer.MAX_VALUE / 2;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i == j) {
                    matrix[i][j] = 0;
                } else {
                    matrix[i][j] = INF;
                }
            }
        }
        if (x != y) {
            matrix[x][y] = 1;
            matrix[y][x] = 1;
        }
        for (int i = 0; i < n - 1; i++) {
            matrix[i][i + 1] = 1;
            matrix[i + 1][i] = 1;
        }
        for (int k = 0; k < n; k++) {
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    matrix[i][j] = Math.min(matrix[i][j], matrix[i][k] + matrix[k][j]);
                }
            }
        }
        int[] result = new int[n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (matrix[i][j] != INF) {
                    result[matrix[i][j]]++;
                }
            }
        }
        return result;
    }
}

