        i += 1
        ans = 0
        while i > 0:
            ans += self.tree[i]
            i -= i & -i
        return ans

class Solution:
    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        answer = []
        n = len(a)
        color_count = [0, 0]
        for color in a:
            color_count[color] += 1

        tree = FenwickTree(n)
        for i, color in enumerate(a):
            tree.update(i, color_count[1 - color])

        for query in queries:
            if query[0] == 1:
                answer.append(tree.query(query[1] - 1))
            else:
                tree.update(query[1], color_count[query[2]])
                color_count[a[query[1]]] -= 1
                color_count[query[2]] += 1
                a[query[1]] = query[2]
        return answer

