            sum[o] += add * (r - l + 1)
            todo[o] += add

        # o=1  [l,r] 1<=l<=r<=n
        # 把 [L,R] 加一，同时返回加一之前的区间和
        def query_and_add1(o: int, l: int, r: int, L: int, R: int) -> int:
            if L > r or R < l:
                return 0
            if L <= l and r <= R:
                do(o, l, r, 1)
                return sum[o]
            m = (l + r) >> 1
            do(o << 1, l, m, 1)
            do(o << 1 | 1, m + 1, r, 1)
            return query_and_add1(o << 1, l, m, L, R) + query_and_add1(o << 1 | 1, m + 1, r, L, R)

        ans = s = 0
        last = {}
        for i, x in enumerate(nums, 1):
            j = last.get(x, 0)
            s += query_and_add1(1, 1, n, j + 1, i) * 2 + i - j
            ans += s
            last[x] = i
        return ans % 1_000_000_007

