        for (auto [x, i]: mp) {
            for (auto [y, j]: mp) {
                if (i < j && (x | y) == ((1 << n) - 1) && (x & y) == 0) {
                    return {min(i, j), max(i, j)};
                }
            }
        }
        return ans;
    }
