class Solution {
public:
    long long matrixSumQueries(int n, vector<vector<int>> &queries) {
        long long ans = 0;
        unordered_set<int> vis[2];
        vector<vector<long long>> matrix(n, vector<long long>(n, 0));
        for (int i = queries.size() - 1; i >= 0; i--) {
            auto &q = queries[i];
            int type = q[0], index = q[1], val = q[2];
            if (!vis[type].count(index)) {
                // 后面（>i）没有对这一行/列的操作
                // 这一行/列还剩下 n-vis[type^1].size() 个可以填入的格子
                //____
                vis[type].insert(index);
                for (int j = 0; j < n; j++) {
                    if (type == 0)
                        matrix[index][j] += val;
                    else
                        matrix[j][index] += val;
                }
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                ans += matrix[i][j];
            }
        }
        return ans;
    }
};

