            q.pop_front();
            if (node.first == hash_node(target)) {
                return node.second;
            }
            for (auto& road : specialRoads) {
                if (node.first == hash_node({road[0], road[1]})) {
                    int next_node = hash_node({road[2], road[3]});
                    if (dist.count(next_node) == 0 || dist[next_node] > node.second + road[4]) {
                        dist[next_node] = node.second + road[4];
                        q.push_back({next_node, dist[next_node]});
                    }
                }
            }
        }
        return -1;
    }

private:
    inline int hash_node(vector<int>& node) {
        return (node[0] << 16) | node[1];
    }
};

