class Solution {
public:
    int minimizeMax(vector<int>& nums, int p) {
        sort(nums.begin(), nums.end());
        int left = -1, right = nums.back() - nums[0];
        while (left + 1 < right) {
            int mid = left + (right - left) / 2;
            int cnt = 0;
            for (int i = 0; i < nums.size() - 1; ++i) {
                if (nums[i + 1] - nums[i] > mid) {
                    cnt += (nums.size() - i - 1);
                    if (cnt >= p) {
                        right = mid;
                        break;
                    }
                }
            }
            if (cnt < p) {
                left = mid;
            }
        }
        return left + 1;
    }
