class FenwickTree:
    def __init__(self, n):
        self.n = n
        self.ft = [0] * n

    def rsq(self, i):
        i += 1
        ans = 0
        while i > 0:
            ans += self.ft[i]
            i -= i & -i
        return ans

    def update(self, i, v):
        i += 1
        while i <= self.n:
            self.ft[i] += v
            i += i & -i


class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        fenwick = FenwickTree(n)
        sl = SortedList(range(n))
        res = []
        for q in queries:
            if q[0] == 1:
                res.append(fenwick.rsq(sl[-q[1]] - 1) - fenwick.rsq(sl[q[1] - 1] - 1))
            else:
                idx = sl.bisect_left(q[1])
                fenwick.update(q[1], -colors[q[1]])
                colors[q[1]] = q[2]
                fenwick.update(q[1], colors[q[1]])
                sl[idx] = q[1]

        return res

