class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        count_zeros = 0
        count_ones = 0
        max_count_ones = 0
        result = 0
        for char in s:
            if char == "0":
                count_zeros += 1
            else:
                count_ones += 1

            if count_zeros * count_zeros <= count_ones:
                max_count_ones = max(max_count_ones, count_ones)
            else:
                result += max_count_ones - count_ones + 1
                count_zeros = 1
                count_ones = 0
                max_count_ones = 0

        if count_zeros * count_zeros <= count_ones:
            max_count_ones = max(max_count_ones, count_ones)
        else:
            result += max_count_ones - count_ones + 1

        return result
