class Solution {
public:
    int firstCompleteIndex(vector<int>& arr, vector<vector<int>>& mat) {
        int n = mat.size();
        int m = mat[0].size();
        unordered_map<int, pair<int, int>> mp;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                mp[mat[i][j]] = {i, j};
            }
        }
        vector<int> rowCnt(n, 0);
        vector<int> colCnt(m, 0);
        for (int i = 0; i < arr.size(); ++i) {
            const auto& [r, c] = mp[arr[i]];
            ++rowCnt[r];
            if (rowCnt[r] == m) {
                return i;
            }
            ++colCnt[c];
            if (colCnt[c] == n) {
                return i;
            }
        }
        return -1;
    }
};

