class Solution {
public:
    string smallestBeautifulString(string s, int k) {
        unordered_set<char> blockedCharacters;
        for (int i = s.length() - 1; i >= 0; --i) {
            blockedCharacters.insert(s[i]);
        }

        string result = "";
        for (int i = 1; i <= k; ++i) {
            char currChar = 'a' + i - 1;
            if (blockedCharacters.find(currChar) == blockedCharacters.end()) {
                result += currChar;
            }
        }
        return result;
    }
};
